mapper.select:
select count(1) from attendance where 1 = 1
and sourceUserId in (select sourceUserId from org_user where org_user.name like '%${attendanceDetailParams.name}%')
and date_format(checkTime,'%Y-%m-%d') = #{attendanceDetailParams.checkTime}

select o1.id,o2.oname as name,o1.createdTime,o1.address as address,o1.baseMacAddr,o1.checkTime from (select id,createdTime,address,baseMacAddr,checkTime,userId from attendance) as o1 left join (select org_user.name oname,org_user.id oid from org_user) as o2 on o2.oid = o1.userId where 1 = 1
and o2.oname like '%${attendanceDetailParams.name}%'
and date_format(checkTime,'%Y-%m-%d') = #{attendanceDetailParams.checkTime}

select count(1) from personal_attendance where 1 = 1


        and userId in (select id from org_user where org_user.name like '%${attendanceDetailParams.name}%')


        and date_format(work_time,'%Y-%m-%d') = #{attendanceDetailParams.checkTime}

select o1.id,o2.oname as name,o3.deptName dept,o1.work_time as workTime,o1.duration from (select id,userId,departmentId,work_time,duration from personal_attendance) as o1 left join (select org_user.name oname,org_user.id oid from org_user) as o2 on o1.userId = o2.oid left join (select id,name as deptName from org_department) as o3 on o1.departmentId = o3.id where 1 = 1
and o2.oname like '%${attendanceDetailParams.name}%'
and date_format(work_time,'%Y-%m-%d') = #{attendanceDetailParams.checkTime}

select count(1) from personal_attendance_detail where 1 = 1

    and userId in (select id from org_user where org_user.name like '%${attendanceDetailParams.name}%')


    and date_format(work_time,'%Y-%m-%d') = #{attendanceDetailParams.checkTime}

select * from personal_attendance_detail where 1 = 1

    and userId in (select id from org_user where org_user.name like '%${attendanceDetailParams.name}%')


    and date_format(work_time,'%Y-%m-%d') = #{attendanceDetailParams.checkTime}
